mapper.select:
select * from accounts

	name like '%${name}%'
	type = #{type}
	createDate >= #{beginDate}
	createDate <= #{endDate}

mapper.update:
update accounts set userId = #{userId},name = #{name},size = #{size},count = #{count},
					unit = #{unit},price = #{price},type = #{type},createDate = #{createDate},
					creator = #{creator} where uuid = #{uuid}
